INSERT [dbo].[News] ([NewsId], [NewsName], [NewsImage], [NewsDescription], [NewsRemark], [NewsMadeby]) VALUES (1, N'Những điều cần biết về thép không gỉ (Inox)', N'/ckfinder/userfiles/files/blog_1.jpg', N'<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Th&eacute;p kh&ocirc;ng gỉ (Inox)&nbsp;l&agrave; một hợp kim của sắt chưa &iacute;t nhất 10.5% Crom. Sự c&oacute; mặt của nguy&ecirc;n tố n&agrave;y gi&uacute;p th&eacute;p c&oacute; khả năng chống lại sự ăn m&ograve;n kim loại của m&ocirc;i trường</span></span></span></span></span></p>
', N'<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>

<figure class="easyimage easyimage-full"><img alt="" src="data:image/jpeg;base64," style="width:1024px" />
<figcaption></figcaption>
</figure>

<p>&nbsp;</p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">&nbsp;1. Th&eacute;p kh&ocirc;ng gỉ l&agrave; g&igrave;?</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Th&eacute;p kh&ocirc;ng gỉ (Inox)&nbsp;l&agrave; một hợp kim của sắt chưa &iacute;t nhất 10.5% Crom. Sự c&oacute; mặt của nguy&ecirc;n tố n&agrave;y gi&uacute;p th&eacute;p c&oacute; khả năng chống lại sự ăn m&ograve;n kim loại của m&ocirc;i trường, Crom được xem như một lớp &aacute;o gi&aacute;p tốt để bảo vệ cho th&eacute;p, lớp Crom c&agrave;ng d&agrave;y th&igrave; khả năng chống oxi h&oacute;a c&agrave;ng cao.</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Th&eacute;p kh&ocirc;ng gỉ kh&ocirc;ng chỉ chứa Crom, m&agrave; c&ograve;n chứa c&aacute;c th&agrave;nh phần kh&aacute;c như Cacbon, Silic v&agrave; Mangan v&agrave; một số c&aacute;c nguy&ecirc;n tố kim loại kh&aacute;c, c&aacute;c th&agrave;nh phần n&agrave;y g&oacute;p phần v&agrave;o việc chống lại gỉ z&eacute;t. Đồng thời, sự c&oacute; mặt của c&aacute;c th&agrave;nh phần n&agrave;y gi&uacute;p kết cấu th&eacute;p bền hơn v&agrave; tạo một lớp m&agrave;ng chống lại sự ăn m&ograve;n kim loại tr&ecirc;n bề mặt của th&eacute;p. Th&eacute;p kh&ocirc;ng gỉ c&oacute; thể chống lại sự oxi h&oacute;a kim loại trong c&aacute;c điều kiện như kh&ocirc;ng kh&iacute;, nước, axit, kiềm v&agrave; c&aacute;c loại chất lỏng kh&aacute;c. C&aacute;c li&ecirc;n kết b&ecirc;n trong của th&eacute;p c&agrave;ng bền th&igrave; khả năng chống x&acirc;m nhập của c&aacute;c chất ăn m&ograve;n c&agrave;ng cao.</span></span>&nbsp;</span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">2. Th&eacute;p kh&ocirc;ng gỉ được ph&aacute;t hiện ra như thế n&agrave;o?</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Th&eacute;p kh&ocirc;ng gỉ được tạo ra lần đầu v&agrave;o năm 1913, do một nh&agrave; luyện kim người Anh, &ocirc;ng l&agrave; Harry Brearley, Harry Brearley đ&atilde; c&oacute; nhiều cuộc thử nghiệm trước đ&oacute; về th&eacute;p v&agrave; cuối c&ugrave;ng &ocirc;ng ph&aacute;t hiện ra rằng, nếu trong th&agrave;nh phần của th&eacute;p chứa 13% Crom th&igrave; th&eacute;p c&oacute; khả năng chống oxi h&oacute;a, ăn m&ograve;n kim loại trong một thời gian d&agrave;i.</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">3. Th&eacute;p kh&ocirc;ng gỉ được ứng dụng trong những lĩnh vực n&agrave;o?</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Loại th&eacute;p n&agrave;y được ứng dụng rộng r&atilde;i trong nhiều lĩnh vực, th&eacute;p kh&ocirc;ng gỉ c&oacute; mặt ở khắp mọi nơi trong ng&ocirc;i nh&agrave; của bạn, từ nh&agrave; bếp, ban c&ocirc;ng, ph&ograve;ng kh&aacute;ch cho tới ph&ograve;ng ngủ. Dưới đ&acirc;y l&agrave; một số ứng dụng của th&eacute;p kh&ocirc;ng gỉ đối với sự ph&aacute;t triển của khoa học, c&ocirc;ng nghệ v&agrave; đời sống con người.&nbsp;</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span></span></p>

<ul>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">L&agrave;m đẹp: dao k&eacute;o, bồn rửa, dụng cụ l&agrave;m t&oacute;c, dao cạo r&acirc;u&hellip;</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Vận tải:&nbsp;hệ thống ống xả, xe lưới, xe bồn, container, t&agrave;u thuyền, xe cộ&hellip;</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">H&oacute;a học, dược phẩm:&nbsp;dụng cụ th&iacute; nghiệm, b&igrave;nh &aacute;p lực&hellip;</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Dầu v&agrave; Gas:&nbsp;b&igrave;nh ga, hệ thống ống dẫn dầu dưới biển&hellip;</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Y tế:&nbsp;dụng cụ phẫu thuật, m&aacute;y qu&eacute;t, m&aacute;y đo...</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Thực phẩm:&nbsp;m&aacute;y m&oacute;c, thiệt bị chế biến, bảo quản thực phẩm&hellip;</span></span></span></span></span></li>
	<li style="text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Nước:&nbsp;hệ thống nước xả, hệ thống xử l&yacute; nước thải, c&aacute;c đường ống chứa thải&hellip;</span></span></span></span></span></li>
</ul>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Ngo&agrave;i ra, n&oacute; c&ograve;n được sử dụng để l&agrave;m c&aacute;c ốc v&iacute;t, bu đ&ocirc;ng, b&agrave;n ghế&hellip;</span></span>&nbsp;</span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">4. Liệu th&eacute;p kh&ocirc;ng gỉ (Inox) c&oacute; bị ăn m&ograve;n kh&ocirc;ng?</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Mặc d&ugrave; Inox c&oacute; khả năng chống lại sự ăn m&ograve;n, oxi h&oacute;a cao hơn c&aacute;c loại th&eacute;p cacbon v&agrave; kim loại kh&aacute;c, nhưng trong một số m&ocirc;i trường đặc biệt, vượt qua ngưỡng bảo vệ của th&eacute;p kh&ocirc;ng gỉ th&igrave; n&oacute; sẽ bị oxi h&oacute;a. Ở m&ocirc;i trường nhiệt độ b&igrave;nh thường, ch&uacute;ng ta c&oacute; thể thấy những vật dụng như dao, k&eacute;o, nồi ni&ecirc;u, xoong chảo kh&ocirc;ng hề bị oxi h&oacute;a, tuy nhi&ecirc;n, khi ch&uacute;ng ta mang những vật dụng đ&oacute; bỏ dưới trời mưa, một thời gian sau ch&uacute;ng sẽ bị gỉ z&eacute;t, hoen ố v&agrave;ng, nguy&ecirc;n nh&acirc;n l&agrave; do trong nước mưa c&oacute; c&aacute;c th&agrave;nh phần axit ăn m&ograve;n kim loại, lớp bảo vệ kim loại như crom sẽ dần bị mất đi.</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif">&nbsp;</span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Thực tế kh&ocirc;ng tồn tại kim loại c&oacute; khả năng chống ăn m&ograve;n tuyệt đối, ngay cả v&agrave;ng v&agrave; bạch kim l&agrave; những kim loại được xem như kh&ocirc;ng bao giờ bị oxi h&oacute;a, nhưng khi gặp c&aacute;c dung dịch c&oacute; chất h&ograve;a tan kim loại mạnh như cường thủy (3 phần axit clohidric + 1 phần axit nitric) vẫn bị oxi h&oacute;a như c&aacute;c kim loại kh&aacute;c. Một t&aacute;c nh&acirc;n cũng ảnh hưởng dẫn đến sự gỉ z&eacute;t của Inox đ&oacute; l&agrave; h&agrave;m lượng c&aacute;c nguy&ecirc;n tố chống ăn m&ograve;n như Crom, Silic, Niken trong inox nhiều hay &iacute;t v&agrave; qu&aacute; tr&igrave;nh xử l&yacute; nhiệt trong qu&aacute; tr&igrave;nh sản xuất c&oacute; ổn định hay kh&ocirc;ng, c&oacute; đủ đảm bảo hay kh&ocirc;ng.</span></span></span></span></span></p>

<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:14px"><span style="background-color:white"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="font-family:&quot;Arial&quot;,sans-serif"><span style="color:black">Khoa học v&agrave; c&ocirc;ng nghệ ng&agrave;y c&agrave;ng ph&aacute;t triển v&agrave; hiện đại hơn, trong tương lai, chắc chắn ch&uacute;ng ta sẽ nghi&ecirc;n cứu v&agrave; t&igrave;m ra được những loại th&eacute;p c&oacute; khả năng chống gỉ tốt hơn, tăng thời gian sử dụng v&agrave; tuổi thọ của những sản phẩm được l&agrave;m từ th&eacute;p.</span></span></span></span></span></p>

